import React, { Component } from 'react';
import './Memo.css';

class Memo extends Component {
  constructor() {
    super();
    this.state = {
      memos: [],
      showModal: false,
      currentMemo: { title: '', content: '', image: null },
      searchTerm: '',
      sortOption: 'recent',
      isDropdownOpen: false, // 드롭다운 메뉴를 열기 위한 상태 변수 추가
      drugNames: [], // API에서 가져온 약 이름 목록을 저장하기 위한 상태 변수 추가
    };
  }

  componentDidMount() {
    this.fetchDrugNames(); // 컴포넌트가 마운트될 때 약 이름 목록을 가져옵니다.
  }

  fetchDrugNames = async () => {
    try {
      const response = await fetch('API_ENDPOINT_HERE'); // API 엔드포인트를 여기에 입력합니다.
      const data = await response.json();
      this.setState({ drugNames: data.drugNames }); // API 응답에서 약 이름 목록을 가져와 상태 변수에 저장합니다.
    } catch (error) {
      console.error('API 호출 중 오류 발생:', error);
    }
  };

  addMemo = () => {
    if (this.state.currentMemo.title.trim() !== '' || this.state.currentMemo.content.trim() !== '') {
      this.setState((prevState) => ({
        memos: [...prevState.memos, prevState.currentMemo],
        currentMemo: { title: '', content: '', image: null },
        showModal: false,
      }));
    }
  };

  deleteMemo = (indexToDelete) => {
    this.setState((prevState) => ({
      memos: prevState.memos.filter((_, index) => index !== indexToDelete),
    }));
  };

  closeModal = () => {
    this.setState({
      showModal: false,
      currentMemo: { title: '', content: '', image: null },
    });
  };

  handleSearch = (e) => {
    this.setState({ searchTerm: e.target.value });
  };

  handleSortChange = (option) => {
    this.setState({ sortOption: option });
  };

  render() {
    return (
      <div className="memo-container">
        <div className="note-wrapper">
          <h1 className="memo-header">약 노트</h1>
        </div>
        <div className="search-and-sort">
          <input
            className="search-bar"
            type="text"
            placeholder="메모 검색"
            value={this.state.searchTerm}
            onChange={this.handleSearch}
          />
          <div className="sort-dropdown">
            <select
              id="sortOptions"
              className="custom-select"
              value={this.state.sortOption}
              onChange={(e) => this.handleSortChange(e.target.value)}
            >
              <option value="recent">최근 생성순</option>
              <option value="alphabetical">가나다순</option>
              <option value="old">오래된 순</option>
            </select>
          </div>
        </div>
        <div className="record-button-wrapper">
          <button className="memo-record-button" onClick={() => this.setState({ showModal: true })}>
            기록하기
          </button>
        </div>
        {this.state.showModal && (
          <div className="memo-modal">
            <div className="memo-modal-content">
              <input
                type="text"
                className="memo-title-input"
                placeholder="제목을 입력하세요"
                value={this.state.currentMemo.title}
                onChange={(e) =>
                  this.setState({
                    currentMemo: { ...this.state.currentMemo, title: e.target.value },
                  })
                }
                onClick={() => this.setState({ isDropdownOpen: true })} 
              />
              {this.state.isDropdownOpen && ( 
                <div className="dropdown-menu">
                  <ul>
                    {this.state.drugNames.map((drugName, index) => (
                      <li key={index}>{drugName}</li>
                    ))}
                  </ul>
                </div>
              )}
              <textarea
                className="memo-content-input"
                placeholder="내용을 입력하세요"
                value={this.state.currentMemo.content}
                onChange={(e) =>
                  this.setState({
                    currentMemo: { ...this.state.currentMemo, content: e.target.value },
                  })
                }
              ></textarea>
              <input
                type="file"
                accept="image/*"
                onChange={(e) =>
                  this.setState({
                    currentMemo: { ...this.state.currentMemo, image: e.target.files[0] },
                  })
                }
              />
              <div className="memo-modal-actions">
                <button className="memo-add-button" onClick={this.addMemo}>
                  추가
                </button>
                <button className="memo-cancel-button" onClick={this.closeModal}>
                  취소
                </button>
              </div>
            </div>
          </div>
        )}
        <div className="memo-list-container">
          <ul className="memo-list">
            {this.state.memos.map((memo, index) => (
              <li key={index} className="memo-item">
                <div className="memo-content">
                  <strong>{memo.title}</strong>
                  <p>{memo.content}</p>
                  {memo.image && <img src={URL.createObjectURL(memo.image)} alt="Memo Image" />}
                </div>
                <button className="memo-delete-button" onClick={() => this.deleteMemo(index)}>
                  삭제
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default Memo;
