import React, { useState } from 'react';
import './Memo.css';

function Memo() {
  const [memos, setMemos] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentMemo, setCurrentMemo] = useState({ title: '', content: '' });

  const addMemo = () => {
    if (currentMemo.title.trim() !== '' && currentMemo.content.trim() !== '') {
      setMemos([...memos, currentMemo]);
      setCurrentMemo({ title: '', content: '' });
      setShowModal(false);
    }
  };

  const deleteMemo = (indexToDelete) => {
    setMemos(memos.filter((_, index) => index !== indexToDelete));
  };

  const closeModal = () => {
    setShowModal(false);
    setCurrentMemo({ title: '', content: '' });
  };

  return (
    <div className="memo-container">
      <h1 className="memo-header">약 노트</h1>
      <button className="memo-record-button" onClick={() => setShowModal(true)}>기록하기</button>
      {showModal && (
        <div className="memo-modal">
          <div className="memo-modal-content">
            <input
              type="text"
              className="memo-title-input"
              placeholder="제목을 입력하세요"
              value={currentMemo.title}
              onChange={(e) => setCurrentMemo({ ...currentMemo, title: e.target.value })}
            />
            <textarea
              className="memo-content-input"
              placeholder="내용을 입력하세요"
              value={currentMemo.content}
              onChange={(e) => setCurrentMemo({ ...currentMemo, content: e.target.value })}
            ></textarea>
            <div className="memo-modal-actions">
              <button className="memo-add-button" onClick={addMemo}>추가</button>
              <button className="memo-cancel-button" onClick={closeModal}>취소</button>
            </div>
          </div>
        </div>
      )}
      <ul className="memo-list">
        {memos.map((memo, index) => (
          <li key={index} className="memo-item">
            <div className="memo-content">
              <strong>{memo.title}</strong>
              <p>{memo.content}</p>
            </div>
            <button className="memo-delete-button" onClick={() => deleteMemo(index)}>삭제</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Memo;
