import React, { useState } from 'react';
import './Memo.css'; // Import a CSS file to style your components

function Memo() {
  const [memos, setMemos] = useState([]);
  const [memoText, setMemoText] = useState('');

  const addMemo = () => {
    if (memoText.trim() !== '') {
      setMemos([...memos, memoText]);
      setMemoText('');
    }
  };

  return (
    <div className="memo-container">
      <h1 className="memo-header">안 노트</h1>
      <div className="memo-input-container">
        <input
          type="text"
          className="memo-input"
          placeholder="메모를 입력하세요"
          value={memoText}
          onChange={(e) => setMemoText(e.target.value)}
        />
        <button className="memo-add-button" onClick={addMemo}>추가</button>
      </div>
      <div className="memo-list-container">
        <ul className="memo-list">
          {memos.map((memo, index) => (
            <li key={index} className="memo-item">{memo}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Memo;
