import React, { useState, useEffect } from 'react';
import './Memo.css';

function Memo() {
  const [memos, setMemos] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentMemo, setCurrentMemo] = useState({ title: '', content: '', image: null });
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('recent');
  const [titleDropdownOptions, setTitleDropdownOptions] = useState([]);
  const [selectedTitleOption, setSelectedTitleOption] = useState('');
  const [drugNames, setDrugNames] = useState([]);

  const predefinedTitleOptions = ['Option 1', 'Option 2', 'Option 3'];

  const addMemo = () => {
    if (currentMemo.title.trim() !== '' || currentMemo.content.trim() !== '') {
      setMemos([...memos, currentMemo]);
      setCurrentMemo({ title: '', content: '', image: null });
      setSelectedTitleOption('');
      setShowModal(false);
    }
  };

  const deleteMemo = (indexToDelete) => {
    setMemos(memos.filter((_, index) => index !== indexToDelete));
  };

  const closeModal = () => {
    setShowModal(false);
    setCurrentMemo({ title: '', content: '', image: null });
    setSelectedTitleOption('');
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = (option) => {
    setSortOption(option);
  };

  const updateTitleDropdownOptions = () => {
    const dropdownOptions = memos.map((memo) => memo.title);
    setTitleDropdownOptions(dropdownOptions);
  };

  useEffect(() => {
    updateTitleDropdownOptions();
  }, [memos]);

  useEffect(() => {
    // Fetch drug names from the API and set them in the drugNames state.
    fetch('YOUR_API_ENDPOINT')
      .then((response) => response.json())
      .then((data) => {
        setDrugNames(data);
      })
      .catch((error) => {
        console.error('Error fetching drug names:', error);
      });
  }, []);

  return (
    <div className="memo-container">
      <div className="note-wrapper">
        <h1 className="memo-header">약 노트</h1>
      </div>
      <div className="search-and-sort">
        <input
          className="search-bar"
          type="text"
          placeholder="메모 검색"
          value={searchTerm}
          onChange={handleSearch}
        />
        <div className="sort-dropdown">
          <select
            id="sortOptions"
            className="custom-select"
            value={sortOption}
            onChange={(e) => handleSortChange(e.target.value)}
          >
            <option value="recent">최근 생성순</option>
            <option value="alphabetical">가나다순</option>
            <option value="old">오래된 순</option>
          </select>
        </div>
      </div>
      <div className="record-button-wrapper">
        <button className="memo-record-button" onClick={() => setShowModal(true)}>
          기록하기
        </button>
      </div>
      {showModal && (
        <div className="memo-modal">
          <div className="memo-modal-content">
            <div className="memo-title-input-container">
              <input
                type="text"
                className="memo-title-input"
                placeholder="약 이름을 입력하세요"
                value={selectedTitleOption || currentMemo.title}
                onChange={(e) => {
                  setSelectedTitleOption(e.target.value);
                  setCurrentMemo({ ...currentMemo, title: e.target.value });
                }}
              />
              {selectedTitleOption && (
                <div className="title-dropdown">
                  <ul>
                    {predefinedTitleOptions.map((option, index) => (
                      <li
                        key={index}
                        onClick={() => {
                          setSelectedTitleOption(option);
                          setCurrentMemo({ ...currentMemo, title: option });
                        }}
                      >
                        {option}
                      </li>
                    ))}
                    {drugNames.map((drugName, index) => (
                      <li
                        key={index}
                        onClick={() => {
                          setSelectedTitleOption(drugName);
                          setCurrentMemo({ ...currentMemo, title: drugName });
                        }}
                      >
                        {drugName}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
            <textarea
              className="memo-content-input"
              placeholder="내용을 입력하세요"
              value={currentMemo.content}
              onChange={(e) => setCurrentMemo({ ...currentMemo, content: e.target.value })}
            ></textarea>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setCurrentMemo({ ...currentMemo, image: e.target.files[0] })}
            />
            <div className="memo-modal-actions">
              <button className="memo-add-button" onClick={addMemo}>
                추가
              </button>
              <button className="memo-cancel-button" onClick={closeModal}>
                취소
              </button>
            </div>
          </div>
        </div>
      )}
      <div className="memo-list-container">
        <ul className="memo-list">
          {memos.map((memo, index) => (
            <li key={index} className="memo-item">
              <div className="memo-content">
                <strong>{memo.title}</strong>
                <p>{memo.content}</p>
                {memo.image && <img src={URL.createObjectURL(memo.image)} alt="Memo Image" />}
              </div>
              <button className="memo-delete-button" onClick={() => deleteMemo(index)}>
                삭제
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Memo;
