import React, { useState } from 'react';
import './LoginForm.css'

function Login() {
  const [userId, setUserId] = useState('');
  const [password, setPassword] = useState('');

  // 사용자 입력 처리 함수
  const handleUserIdChange = (e) => setUserId(e.target.value);
  const handlePasswordChange = (e) => setPassword(e.target.value);

  // 로그인 요청 처리 함수
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8080/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          id: userId,
          password: password,
        }),
      });
      if (response.status === 200) {
        alert('로그인 성공!!');
        // 추가적인 로그인 성공 처리
      } else {
        alert('다시 확인하세요.');
        // 실패 처리
      }
    } catch (error) {
      console.error('로그인 요청 실패:', error);
      alert('다시 확인하세요.');
    }
  };

  return (
    <div className="sign-in-htm">
      <form onSubmit={handleSubmit}>
        <div className="group">
          <label htmlFor="user" className="label">
            아이디
          </label>
          <input id="user" type="text" className="input" value={userId} onChange={handleUserIdChange} />
        </div>
        <div className="group">
          <label htmlFor="pass" className="label">
            비밀번호
          </label>
          <input id="pass" type="password" className="input" data-type="password" value={password} onChange={handlePasswordChange} />
        </div>
        <div className="group">
          <input type="submit" className="button" value="로그인" />
        </div>
      </form>
      <div className="hr"></div>
      <div className="foot-lnk">
        <label htmlFor="tab-2">계정이 없다면 회원가입을 해주세요.</label>
      </div>
    </div>
  );
}

export default Login;
