import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Login from './Login';
import Signup from './Signup';
import Memo from './Memo';

function AppWrapper() {
  const location = useLocation();
  const isMemoPage = location.pathname === '/memo';

  return (
    <div className={isMemoPage ? "login-body memo-page" : "login-body"}>
      <div className={isMemoPage ? "login-wrap memo-page" : "login-wrap"}>
        <div className="login-html">
          {!isMemoPage && (
            <>
              <input id="tab-1" type="radio" name="tab" className="sign-in" defaultChecked />
              <label htmlFor="tab-1" className="tab">로그인</label>
              <input id="tab-2" type="radio" name="tab" className="sign-up" />
              <label htmlFor="tab-2" className="tab">회원가입</label>
            </>
          )}
          <div className="login-form">
            <Routes>
              <Route path="/" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="/memo" element={<Memo />} />
            </Routes>
          </div>
        </div>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppWrapper />
    </Router>
  );
}

export default App;
