import React, { useState, useEffect } from 'react';
import './Memo.css';

function Memo() {
  const [memos, setMemos] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [currentMemo, setCurrentMemo] = useState({ title: '', content: '', image: null });
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('recent');
  const [medicationTypes, setMedicationTypes] = useState([]);
  const [selectedMedicationType, setSelectedMedicationType] = useState('');

  useEffect(() => {
    // 약의 종류를 가져오는 API 요청
    const fetchMedicationTypes = async () => {
      try {
        const response = await fetch('YOUR_API_ENDPOINT_FOR_MEDICATION_TYPES');
        if (!response.ok) {
          throw new Error('약의 종류를 가져오는 데 실패했습니다.');
        }
        const data = await response.json();
        // API 응답이 약의 종류를 포함하는 배열이라고 가정합니다.
        setMedicationTypes(data);
      } catch (error) {
        console.error('약의 종류를 가져오는 중 오류가 발생했습니다:', error);
      }
    };

    // 컴포넌트가 마운트될 때 약의 종류를 가져옵니다.
    fetchMedicationTypes();
  }, []);

  const addMemo = () => {
    if (currentMemo.title.trim() !== '' || currentMemo.content.trim() !== '') {
      const memoWithMedicationType = {
        ...currentMemo,
        medicationType: selectedMedicationType,
      };
      setMemos([...memos, memoWithMedicationType]);
      setCurrentMemo({ title: '', content: '', image: null });
      setShowModal(false);
      setSelectedMedicationType('');
    }
  };

  const deleteMemo = (indexToDelete) => {
    setMemos(memos.filter((_, index) => index !== indexToDelete));
  };

  const closeModal = () => {
    setShowModal(false);
    setCurrentMemo({ title: '', content: '', image: null });
    setSelectedMedicationType('');
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSortChange = (option) => {
    setSortOption(option);
  };

  return (
    <div className="memo-container">
      <div className="note-wrapper">
        <h1 className="memo-header">약 노트</h1>
      </div>
      <div className="search-and-sort">
        <input
          className="search-bar"
          type="text"
          placeholder="메모 검색"
          value={searchTerm}
          onChange={handleSearch}
        />
        <div className="sort-dropdown">
          <select
            id="sortOptions"
            className="custom-select"
            value={sortOption}
            onChange={(e) => handleSortChange(e.target.value)}
          >
            <option value="recent">최근 생성순</option>
            <option value="alphabetical">가나다순</option>
            <option value="old">오래된 순</option>
          </select>
        </div>
      </div>
      <div className="record-button-wrapper">
        <button className="memo-record-button" onClick={() => setShowModal(true)}>
          기록하기
        </button>
      </div>
      {showModal && (
        <div className="memo-modal">
          <div className="memo-modal-content">
            {/* 제목 입력 칸 대신 커스텀 컴포넌트 사용 */}
            <MedicationTypeDropdown
              selectedMedicationType={selectedMedicationType}
              setSelectedMedicationType={setSelectedMedicationType}
              medicationTypes={medicationTypes}
            />
            <textarea
              className="memo-content-input"
              placeholder="내용을 입력하세요"
              value={currentMemo.content}
              onChange={(e) => setCurrentMemo({ ...currentMemo, content: e.target.value })}
            ></textarea>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setCurrentMemo({ ...currentMemo, image: e.target.files[0] })}
            />
            <div className="memo-modal-actions">
              <button className="memo-add-button" onClick={addMemo}>
                추가
              </button>
              <button className="memo-cancel-button" onClick={closeModal}>
                취소
              </button>
            </div>
          </div>
        </div>
      )}
      <div className="memo-list-container">
        <ul className="memo-list">
          {memos.map((memo, index) => (
            <li key={index} className="memo-item">
              <div className="memo-content">
                <strong>{memo.title}</strong>
                <p>{memo.content}</p>
                {memo.image && <img src={URL.createObjectURL(memo.image)} alt="Memo Image" />}
                {memo.medicationType && <p>약의 종류: {memo.medicationType}</p>}
              </div>
              <button className="memo-delete-button" onClick={() => deleteMemo(index)}>
                삭제
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

// 약의 종류를 선택할 수 있는 드롭다운 컴포넌트 정의
function MedicationTypeDropdown({ selectedMedicationType, setSelectedMedicationType, medicationTypes }) {
  return (
    <select
      className="memo-medication-type"
      value={selectedMedicationType}
      onChange={(e) => setSelectedMedicationType(e.target.value)}
    >
      <option value="">약의 종류 선택</option>
      {medicationTypes.map((type) => (
        <option key={type.id} value={type.name}>
          {type.name}
        </option>
      ))}
    </select>
  );
}

export default Memo;
